import React, { Component } from "react";

export default class Hangghe extends Component {
  renderGhe = () => {
    return this.props.hangGhe.danhSachGhe.map((ghe, index) => {
      if(index === 0){
          return <span className="rowNumber">{ghe.soGhe}</span>
      }

      let cssGheDaDat = "";
      let disabled = false;
      if (ghe.daDat) {
        cssGheDaDat = "gheDuocChon";
        disabled = true;
      }
      return (
        <button
          onClick={() => {
            alert(123);
          }}
          disabled={disabled}
          className={`ghe ${cssGheDaDat}`}
          key={index}
        >
          {ghe.soGhe}
        </button>
      );
    });
  };
  renderSoHang=()=> {
      return <span className="rowNumber">
          {this.props.hangGhe.ten}
      </span>
  }
  renderHangGhe=()=>{
      if(this.props.soHangGhe === 0){
        return <div>
        {this.props.hangGhe.hang} {this.renderSoHang()};
        </div>
      } else{
        return <div>
          {this.props.hangGhe.hang} {this.renderGhe()}
      </div>
      }



  }

  render() {
    return (
      <div className="text-light text-left ml-5 mt-3">
        {this.renderHangGhe()}
      </div>
    );
  }
}
