import React, { Component } from "react";
import {connect} from 'react-redux'

 class Hangghe extends Component {
  renderGhe = () => {
    return this.props.hangGhe.danhSachGhe.map((ghe, index) => {
      if (index === 0) {
        return <span className="rowNumber">{ghe.soGhe}</span>;
      }

      let cssGheDaDat = "";
      let disabled = false;
      if (ghe.daDat) {
        cssGheDaDat = "gheDuocChon";
        disabled = true;
      }
      return (
        <button
          onClick={() => {
            alert(123);
          }}
          disabled={disabled}
          className={`ghe ${cssGheDaDat}`}
          key={index}
        >
          {ghe.soGhe}
        </button>
      );
    });
  };
  renderSoHang = () => {
    return this.props.hangGhe.danhSachGhe.map((hang, index) => {
      return <button className="rowNumber">{hang.soGhe}</button>
    });
  };
  renderHangGhe = () => {
    if (this.props.soHangGhe === 0) {
      return (
        <div className="ml-2"> 
          {this.props.hangGhe.hang} {this.renderSoHang()};
        </div>
      );
    } else {
      return (
        <div>
          {this.props.hangGhe.hang} {this.renderGhe()}
        </div>
      );
    }
  };

  render() {
    return (
      <div className="text-light text-left ml-5 mt-1 ">
        {this.renderHangGhe()}
      </div>
    )
  }
}

const mapStateToProps = state => {
    return {
        danhSachGheDangDat: state.BookingMovieReducer.danhSachGheDangDat
    }
}

export default con (Hangghe)
