import React, { Component } from 'react'

export default class Computer extends Component {
    render() {
        return (
            <div className="text-center playerGame">

            <div className="think">
            <img style={{transform:'rotate(90deg)'}} className="mt-3" style={{width:100, height:100}} 
                src="./img/gameoantuxi/bua1.png" 
                alt="./img/gameoantuxi/bua.png"/>

            </div>

            <div className="speech-bubble"></div>
            <img style={{width:150, height:150}} 
            src="./img/gameoantuxi/playerComputer.png" 
            alt="./img/gameoantuxi/playerComputer.png"/>
        </div>
        )
    }
}
