import React, { Component } from 'react'

export default class FormVadiation extends Component {
    constructor(props) {
        super(props)
        this.state = {
            manv: "",
            tennv: "",
            email: "",
        }
        this.close = React.createRef();
    }

    handleOnChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
        }, );
    }
    handleSubmit = (event) => {
        event.preventDefault();
        // this.props.getUserSubmit(this.state);
        console.log(this.state)

        //close modal
        // this.close.current.click();
      };

    render() {
        return (
            <div className="container">
            <h3 className="title">*FormValidation</h3>
            <form onSubmit={this.handleSubmit}>
              <div className="form-group">
                <label>Mã Nhân Viên</label>
                <input type="text" className="form-control" name="manv" onChange={this.handleOnChange} />
              </div>
              <div className="form-group">
                <label>Tên Nhân Viên</label>
                <input type="text" className="form-control" name="tennv" 
                onChange={this.handleOnChange} />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input type="email" className="form-control" name="email"
                onChange={this.handleOnChange} />
              </div>
              <button type="submit" className="btn btn-success">
                Submit
              </button>
            </form>
          </div>
        )
    }
}
