import React, { Component } from 'react' 
import Child from './child';
export default class Lifecycle extends Component {
    constructor(props) {
        super(props);
        //constructor chạy đúng 1 lần duy nhất trong quá trình chạy component thực thi
        console.log("constructor")
        this.state = { 
            number: 1,
            number1: 3
        }
    }

    UNSAFE_componentWillMount() {
        //componentWillMount chạy đúng 1 lần duy nhất trong quá trình chạy component thực thi
        // Fetch data (gọi Api)
        console.log("componentWillMount")
    }

    componentDidMount() {
        //componentDidMount chạy đúng 1 lần duy nhất trong quá trình chạy component thực thi
        console.log("componentDidMount");
    }

    UNSAFE_componentWillUpdate() {
        console.log("componentWillUpdate")
    }
    componentDidUpdate() {
        console.log("componentDidUpdate")
    }
    shouldComponentUpdate(nextProps, nextState) {
        console.log("shouldComponentUpdate", nextProps, nextState)
        if(nextState && nextState.number === 2) {
            return false;
        }
        return true;
    }

    render() {
        console.log("render")
        return (
            <div>
                <h3> Lifecycle</h3>
                <h1>Number: {this.state.number}</h1>
                <h2>number1: this.state.number1</h2>
                <button className="btn btn-success" onClick={()=> {
                    this.setState({
                        number: this.state.number * 2,
                        number1: this.state.number1 + 1
                    })
                }}>Increment</button>

                <hr/>
                <Child/>
            </div>

        )   
    }   
}
