    componentWillUnmount() {
        /**
         * Khi coponent bị huỷ thì mới thực thi 
         */
        console.log("componentWillUnmount")
        clearInterval(this.interval)
    }

    render() {
        return (
            <div>
                <h3>Child 2</h3>
            </div>
        )
    }
}
