const stateDefault = {
    mangDatCuoc: [
        {ma: 'keo', hinhAnh: './img/gameoantuxi/keo.png', datCuoc: false},
        {ma: 'bua', hinhAnh: './img/gameoantuxi/bua.png', datCuoc: false},
        {ma: 'bao', hinhAnh: './img/gameoantuxi/bao.png', datCuoc: false}
    ],
    ketQua: "Nguyễn Thị Thuý Kiều",
    soBanThang: 0,
    tongSoBanChoi: 0,
    computer: {ma: 'bua', hinhAnh: './img/gameoantuxi/bua1.png'},
}

const oanTuXiReducer = (state = stateDefault,action) => {
    switch(action.type){
        case 'Chon_keo_bua_bao': {
            let mangCuocUpdate = [...this.state.mangDatCuoc]
            mangCuocUpdate = mangCuocUpdate.map((item, index) => { return{...item, datCuoc:false}})
            console.log('mangCuocUpdate',mangCuocUpdate)

            let index = mangCuocUpdate.findIndex(qc => qc.ma === action.mangCuoc)

            if(index !== -1){

                mangCuocUpdate[index].datCuoc = true;

            }

            state.mangDatCuoc = mangCuocUpdate;
            return{...state}
        }

        default: return{...state}
    }
}

export default oanTuXiReducer;
