import React, { Component } from "react";
import { connect } from "react-redux";

class Player extends Component {
  render() {
    console.log("prop", this.props.mangDatCuoc);
    return (
      <div className="text-center playerGame">
        <div className="think">
          <img
            className="mt-3"
            style={{ width: 100, height: 100 }}
            src={this.props.mangDatCuoc.find(item=>item.datCuoc === true).hinhAnh}
            alt={this.props.mangDatCuoc.find(item=>item.datCuoc === true).hinhAnh}
          />
        </div>

        <div className="speech-bubble"></div>
        <img
          style={{ width: 150, height: 150 }}
          src="./img/gameoantuxi/player.png"
          alt="./img/gameoantuxi/player.png"
        />

        <div className="row">
            {this.props.mangDatCuoc.map((item, index)=>{

                let border = {};
                if(item.datCuoc){
                    border = {border: '3px solid orange'}
                }

                return <div className="col-4">
                <button onClick={()=>{

                }}
                 style={border} className="btnItem">
                  <img
                    style={{ width: 35, height: 35 }}
                    src={item.hinhAnh}
                    alt={item.hinhAnh}
                  />
                </button>
              </div>

            })}


        </div>
      </div>
    );
  }

}
const mapStateToProps = (state) => {
    return {
      mangDatCuoc: state.oanTuXiReducer.mangDatCuoc,
    };
  };

const mapDispatchToProps = (dispatch) => {
    return {
        datCuoc: (maCuoc) => {
            dispatch({
                type: 'Chon_keo_bua_bao'
            })
        }
    }
}  
export default connect(mapStateToProps)(Player);
